import React, { useState } from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Button, 
  Box, 
  IconButton, 
  Menu, 
  MenuItem, 
  Container
} from '@mui/material';
import { 
  Pets, 
  Menu as MenuIcon, 
  AccountCircle
} from '@mui/icons-material';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Header = () => {
  const navigate = useNavigate();
  const { user, logout, isAuthenticated, isOwner } = useAuth();
  const [anchorEl, setAnchorEl] = useState(null);
  const [mobileMenuAnchor, setMobileMenuAnchor] = useState(null);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMobileMenuOpen = (event) => {
    setMobileMenuAnchor(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    setMobileMenuAnchor(null);
  };

  const handleLogout = () => {
    logout();
    handleMenuClose();
    navigate('/');
  };

  const menuId = 'primary-search-account-menu';
  const mobileMenuId = 'primary-search-account-menu-mobile';

  return (
    <AppBar position="static">
      <Container maxWidth="lg">
        <Toolbar>
          {/* Logo */}
          <Box 
            component={Link} 
            to="/" 
            sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              textDecoration: 'none', 
              color: 'inherit',
              flexGrow: 1 
            }}
          >
            <Pets sx={{ mr: 1 }} />
            <Typography variant="h6" component="div">
              Питомник Zelenas
            </Typography>
          </Box>

          {/* Desktop Navigation */}
          <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 2 }}>
            <Button 
              color="inherit" 
              component={Link} 
              to="/puppies"
            >
              Щенки
            </Button>
            <Button 
              color="inherit" 
              component={Link} 
              to="/news"
            >
              Новости
            </Button>
            {isOwner && (
              <Button 
                color="inherit" 
                component={Link} 
                to="/dashboard"
              >
                Управление
              </Button>
            )}
          </Box>

          {/* Auth Buttons / User Menu */}
          <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 1 }}>
            {!isAuthenticated ? (
              <Button 
                color="inherit" 
                component={Link} 
                to="/login"
              >
                Вход для владельца
              </Button>
            ) : (
              <>
                <IconButton
                  size="large"
                  edge="end"
                  aria-label="account of current user"
                  aria-controls={menuId}
                  aria-haspopup="true"
                  onClick={handleProfileMenuOpen}
                  color="inherit"
                >
                  <AccountCircle />
                </IconButton>
                <Menu
                  anchorEl={anchorEl}
                  id={menuId}
                  keepMounted
                  transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                  }}
                  open={Boolean(anchorEl)}
                  onClose={handleMenuClose}
                >
                  <MenuItem 
                    onClick={() => { handleMenuClose(); navigate('/profile'); }}
                  >
                    Профиль
                  </MenuItem>
                  <MenuItem onClick={handleLogout}>
                    Выйти
                  </MenuItem>
                </Menu>
              </>
            )}
          </Box>

          {/* Mobile Menu Button */}
          <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="show more"
              aria-controls={mobileMenuId}
              aria-haspopup="true"
              onClick={handleMobileMenuOpen}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              anchorEl={mobileMenuAnchor}
              id={mobileMenuId}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(mobileMenuAnchor)}
              onClose={handleMenuClose}
            >
              <MenuItem 
                onClick={() => { handleMenuClose(); navigate('/puppies'); }}
              >
                Щенки
              </MenuItem>
              <MenuItem 
                onClick={() => { handleMenuClose(); navigate('/news'); }}
              >
                Новости
              </MenuItem>
              {isOwner && (
                <MenuItem 
                  onClick={() => { handleMenuClose(); navigate('/dashboard'); }}
                >
                  Управление
                </MenuItem>
              )}
              {isAuthenticated ? (
                <>
                  <MenuItem 
                    onClick={() => { handleMenuClose(); navigate('/profile'); }}
                  >
                    Профиль
                  </MenuItem>
                  <MenuItem onClick={handleLogout}>
                    Выйти
                  </MenuItem>
                </>
              ) : (
                <MenuItem 
                  onClick={() => { handleMenuClose(); navigate('/login'); }}
                >
                  Вход для владельца
                </MenuItem>
              )}
            </Menu>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Header;
