import React, { useEffect, useState } from 'react';

function Dogs() {
  const [dogs, setDogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/dogs')
      .then(res => res.json())
      .then(data => {
        setDogs(data);
        setLoading(false);
      })
      .catch(err => {
        setDogs([]);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Загрузка...</div>;

  return (
    <div className="dogs-page" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '60vh' }}>
      <h1 style={{ textAlign: 'center', width: '100%' }}>Наши собаки</h1>
      <div className="dogs-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '24px', width: '100%', marginTop: 24 }}>
        {dogs.length === 0 && <p style={{ textAlign: 'center', width: '100%' }}>Пока нет информации о собаках.</p>}
        {dogs.map(dog => (
          <div key={dog.id} className="dog-card" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', maxWidth: 320, minWidth: 220, boxShadow: '0 2px 8px #eee', borderRadius: 12, padding: 16, background: '#fff' }}>
            {dog.photo && (
              <img src={`/${dog.photo.replace('\\', '/')}`} alt={dog.name} style={{maxWidth: 200, borderRadius: 8, marginBottom: 12}} />
            )}
            <h2 style={{ textAlign: 'center', margin: '8px 0' }}>{dog.name}</h2>
            {dog.achievements && <p style={{ textAlign: 'center', fontWeight: 'bold', margin: '4px 0' }}>Достижения: <span style={{ fontWeight: 'normal' }}>{dog.achievements}</span></p>}
            {dog.description && <p style={{ textAlign: 'center', margin: '4px 0' }}>{dog.description}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dogs;
