import React, { useEffect, useState } from 'react';

function Dogs() {
  const [dogs, setDogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/dogs')
      .then(res => res.json())
      .then(data => {
        setDogs(data);
        setLoading(false);
      })
      .catch(err => {
        setDogs([]);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Загрузка...</div>;

  return (
    <div className="dogs-page">
      <h1 style={{ textAlign: 'center' }}>Наши собаки</h1>
      <div className="dogs-list">
        {dogs.length === 0 && <p>Пока нет информации о собаках.</p>}
        {dogs.map(dog => (
          <div key={dog.id} className="dog-card">
            {dog.photo && (
              <img src={`/${dog.photo.replace('\\', '/')}`} alt={dog.name} style={{maxWidth: 200}} />
            )}
            <h2>{dog.name}</h2>
            {dog.achievements && <p><b>Достижения:</b> {dog.achievements}</p>}
            {dog.description && <p>{dog.description}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dogs;
