import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Box,
  TextField,
  Button
} from '@mui/material';

function News() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    title: '',
    content: '',
    image: null
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchNews();
  }, []);

  const fetchNews = () => {
    setLoading(true);
    fetch('/api/news')
      .then(res => res.json())
      .then(data => {
        setNews(data.news || data);
        setLoading(false);
      })
      .catch(err => {
        setNews([]);
        setLoading(false);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setForm(prev => ({ ...prev, image: file }));
    setImagePreview(file ? URL.createObjectURL(file) : null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    const formData = new FormData();
    formData.append('title', form.title);
    formData.append('content', form.content);
    if (form.image) formData.append('image', form.image);
    try {
      const res = await fetch('/api/news', {
        method: 'POST',
        body: formData
      });
      if (!res.ok) {
        const data = await res.json();
        setError(data.message || 'Ошибка при добавлении');
        return;
      }
      setForm({ title: '', content: '', image: null });
      setImagePreview(null);
      fetchNews();
    } catch (err) {
      setError('Ошибка при добавлении');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Удалить новость?')) return;
    await fetch(`/api/news/${id}`, { method: 'DELETE' });
    fetchNews();
  };

  if (loading) return (
    <Container>
      <Typography>Загрузка...</Typography>
    </Container>
  );

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          Новости питомника
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mb: 4, p: 2, border: '1px solid #eee', borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom>Добавить новость</Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Заголовок" name="title" value={form.title} onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12}>
              <TextField label="Содержание" name="content" value={form.content} onChange={handleChange} fullWidth required multiline rows={3} />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <Button variant="outlined" component="label" fullWidth>
                Загрузить изображение
                <input type="file" hidden accept="image/*" onChange={handleImageChange} />
              </Button>
              {imagePreview && (
                <Box sx={{ mt: 1 }}>
                  <img src={imagePreview} alt="preview" style={{ width: 100, borderRadius: 8 }} />
                </Box>
              )}
            </Grid>
            {error && (
              <Grid item xs={12}>
                <Typography color="error">{error}</Typography>
              </Grid>
            )}
            <Grid item xs={12}>
              <Button type="submit" variant="contained">Добавить</Button>
            </Grid>
          </Grid>
        </Box>
        <Grid container spacing={4}>
          {news.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item.id || item._id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', position: 'relative' }}>
                {item.image && (
                  <CardMedia
                    component="img"
                    height="200"
                    image={item.image.startsWith('/') ? item.image : `/${item.image}`}
                    alt={item.title}
                  />
                )}
                <CardContent>
                  <Typography variant="h6" gutterBottom align="center">
                    {item.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" align="center" paragraph>
                    {item.content}
                  </Typography>
                  <Typography variant="caption" color="text.secondary" align="center" display="block">
                    {item.createdAt ? new Date(item.createdAt).toLocaleDateString('ru-RU') : ''}
                  </Typography>
                  <Button color="error" variant="outlined" size="small" sx={{ mt: 2 }} onClick={() => handleDelete(item.id || item._id)}>
                    Удалить
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
        {news.length === 0 && (
          <Box textAlign="center" sx={{ mt: 4 }}>
            <Typography variant="h6" color="text.secondary">
              Пока нет новостей.
            </Typography>
          </Box>
        )}
      </Box>
    </Container>
  );
}

export default News;
