import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Box,
  TextField,
  Button,
  MenuItem
} from '@mui/material';

function Puppies() {
  const [puppies, setPuppies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    name: '',
    breed: '',
    age: '',
    price: '',
    description: '',
    gender: '',
    color: '',
    images: []
  });
  const [imagePreviews, setImagePreviews] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPuppies();
  }, []);

  const fetchPuppies = () => {
    setLoading(true);
    fetch('/api/puppies')
      .then(res => res.json())
      .then(data => {
        setPuppies(data);
        setLoading(false);
      })
      .catch(err => {
        setPuppies([]);
        setLoading(false);
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setForm(prev => ({ ...prev, images: files }));
    setImagePreviews(files.map(file => URL.createObjectURL(file)));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('breed', form.breed);
    formData.append('age', form.age);
    formData.append('price', form.price);
    formData.append('description', form.description);
    formData.append('gender', form.gender);
    formData.append('color', form.color);
    form.images.forEach(img => formData.append('images', img));
    try {
      const res = await fetch('/api/puppies', {
        method: 'POST',
        body: formData
      });
      if (!res.ok) {
        const data = await res.json();
        setError(data.message || 'Ошибка при добавлении');
        return;
      }
      setForm({ name: '', breed: '', age: '', price: '', description: '', gender: '', color: '', images: [] });
      setImagePreviews([]);
      fetchPuppies();
    } catch (err) {
      setError('Ошибка при добавлении');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Удалить щенка?')) return;
    await fetch(`/api/puppies/${id}`, { method: 'DELETE' });
    fetchPuppies();
  };

  if (loading) return (
    <Container>
      <Typography>Загрузка...</Typography>
    </Container>
  );

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          Щенки на продажу
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mb: 4, p: 2, border: '1px solid #eee', borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom>Добавить щенка</Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Имя" name="name" value={form.name} onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Порода" name="breed" value={form.breed} onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Возраст (мес.)" name="age" value={form.age} onChange={handleChange} type="number" fullWidth required />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Цена (₽)" name="price" value={form.price} onChange={handleChange} type="number" fullWidth required />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Описание" name="description" value={form.description} onChange={handleChange} fullWidth required multiline rows={2} />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <TextField select label="Пол" name="gender" value={form.gender} onChange={handleChange} fullWidth required>
                <MenuItem value="male">Мальчик</MenuItem>
                <MenuItem value="female">Девочка</MenuItem>
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <TextField label="Цвет" name="color" value={form.color} onChange={handleChange} fullWidth required />
            </Grid>
            <Grid item xs={12} sm={6} md={4}>
              <Button variant="outlined" component="label" fullWidth>
                Загрузить фото
                <input type="file" hidden multiple accept="image/*" onChange={handleImageChange} />
              </Button>
              <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                {imagePreviews.map((src, i) => (
                  <img key={i} src={src} alt="preview" style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 4 }} />
                ))}
              </Box>
            </Grid>
            {error && (
              <Grid item xs={12}>
                <Typography color="error">{error}</Typography>
              </Grid>
            )}
            <Grid item xs={12}>
              <Button type="submit" variant="contained">Добавить</Button>
            </Grid>
          </Grid>
        </Box>
        <Grid container spacing={4}>
          {puppies.map((puppy) => (
            <Grid item xs={12} sm={6} md={4} key={puppy.id || puppy._id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', position: 'relative' }}>
                {puppy.images && puppy.images[0] && (
                  <CardMedia
                    component="img"
                    height="200"
                    image={puppy.images[0].startsWith('/') ? puppy.images[0] : `/${puppy.images[0]}`}
                    alt={puppy.name}
                  />
                )}
                <CardContent>
                  <Typography variant="h6" gutterBottom align="center">
                    {puppy.name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" align="center" gutterBottom>
                    {puppy.breed} • {puppy.age} мес.
                  </Typography>
                  <Typography variant="body2" color="text.secondary" align="center" paragraph>
                    {puppy.description}
                  </Typography>
                  <Typography variant="h6" color="primary" align="center">
                    {puppy.price ? puppy.price.toLocaleString() : ''} ₽
                  </Typography>
                  <Typography variant="body2" color="text.secondary" align="center">
                    {puppy.gender === 'male' ? 'Мальчик' : puppy.gender === 'female' ? 'Девочка' : ''}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" align="center">
                    {puppy.color}
                  </Typography>
                  <Button color="error" variant="outlined" size="small" sx={{ mt: 2 }} onClick={() => handleDelete(puppy.id || puppy._id)}>
                    Удалить
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
        {puppies.length === 0 && (
          <Box textAlign="center" sx={{ mt: 4 }}>
            <Typography variant="h6" color="text.secondary">
              Пока нет информации о щенках.
            </Typography>
          </Box>
        )}
      </Box>
    </Container>
  );
}

export default Puppies;
